foreach ($tourId in $tours.Keys) {
    echo "Processing $tourId"
    $urls = $tours[$tourId]
    $count = 1
    
    foreach ($url in $urls) {
        $filename = "assets/images/tours/$tourId/" + "{0:D2}.jpg" -f $count
        echo "Downloading $url to $filename"
        try {
            # Use 'curl' equivalent for potentially better handling or straight Invoke-WebRequest
            # Note: Some URLs are from Googleusercontent which might expire or block, but we try.
            # Using browser UserAgent sometimes helps.
            Invoke-WebRequest -Uri $url -OutFile $filename -UserAgent "Mozilla/5.0" -ErrorAction Stop
        } catch {
            echo "Failed to download $url : $_"
        }
        $count++
    }
}
